pragma solidity ^0.8.9;

// import "hardhat/console.sol";

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";

import "hardhat/console.sol";

contract Bridge is Pausable, AccessControl {
  using ECDSA for bytes32;

  bytes32 public constant SIGNATORY_ROLE = keccak256("SIGNATORY_ROLE");
  bytes32 public constant ADMIN_ROLE = keccak256("ADMIN_ROLE");

  // chainId (from) where tokens were locked => unique transaction hash => completed/not (boolean)
  mapping(uint256 => mapping(bytes32 => bool)) completedTransactions;
  mapping(address => Transaction[]) sentTransactions;
  mapping(address => Transaction[]) gotTransactions;

  uint256[] supportedChains;

  IERC20 public token;

  constructor(
    address _verifier,
    address _token,
    uint256[] memory _supportedChains
  ) {
    token = IERC20(_token);
    supportedChains = _supportedChains;
    _grantRole(SIGNATORY_ROLE, _verifier);
    _grantRole(ADMIN_ROLE, msg.sender);
  }

  struct Transaction {
    uint256 timestamp; // as unique id to identify similar operations
    address user; // for check on other chains
    uint256 amount;
    uint256 chainIdFrom;
    uint256 chainIdTo;
  }

  event TokensSent(
    uint256 indexed timestamp,
    address indexed user,
    uint256 amount,
    uint256 indexed chainIdFrom,
    uint256 chainIdTo
  );

  event TokensGot(
    uint256 indexed timestamp,
    address indexed user,
    uint256 amount,
    uint256 indexed chainId
  );

  /* _-_-_-_-_-_-_-_-_-_-_- main _-_-_-_-_-_-_-_-_-_-_- */

  function sendTokens(uint256 amount, uint256 toChain) external whenNotPaused {
    require(
      toChain != block.chainid,
      "You've set chain id of network you're currently on."
    );
    require(amount > 0, "Zero amount.");
    require(chainIsSupported(toChain), "Chain is not supported yet.");

    token.transferFrom(msg.sender, address(this), amount);

    sentTransactions[msg.sender].push(
      Transaction(block.timestamp, msg.sender, amount, block.chainid, toChain)
    );

    emit TokensSent(
      block.timestamp,
      msg.sender,
      amount,
      block.chainid,
      toChain
    );
  }

  function getTokens(
    Transaction memory transaction,
    bytes memory signature
  ) external whenNotPaused {
    require(transaction.user == msg.sender, "Not correct user.");
    require(
      transaction.chainIdTo == block.chainid,
      "Not correct chain id to get tokens."
    );

    bytes32 hash = keccak256(abi.encode(transaction));

    require(
      !completedTransactions[transaction.chainIdFrom][hash],
      "Transaction already fulfilled."
    );
    completedTransactions[transaction.chainIdFrom][hash] = true;
    gotTransactions[msg.sender].push(transaction);

    // verifying signature
    require(verifySignature(hash, signature), "Wrong signature.");

    token.transfer(msg.sender, transaction.amount);

    emit TokensGot(
      block.timestamp,
      msg.sender,
      transaction.amount,
      block.chainid
    );
  }

  /* _-_-_-_-_-_-_-_-_-_-_- getters _-_-_-_-_-_-_-_-_-_-_- */

  function getTransactions(
    address user
  ) public view returns (Transaction[] memory, Transaction[] memory) {
    return (sentTransactions[user], gotTransactions[user]);
  }

  /* _-_-_-_-_-_-_-_-_-_-_- chainids _-_-_-_-_-_-_-_-_-_-_- */

  function chainIsSupported(uint256 element) internal view returns (bool) {
    for (uint256 i; i < supportedChains.length; ++i)
      if (supportedChains[i] == element) return true;
    return false;
  }

  function setSupportedChains(
    uint256[] memory chainIds
  ) external onlyRole(ADMIN_ROLE) {
    supportedChains = chainIds;
  }

  /* _-_-_-_-_-_-_-_-_-_-_- crypto _-_-_-_-_-_-_-_-_-_-_-_ */

  /**
   * see https://docs.openzeppelin.com/contracts/2.x/utilities#cryptography
   * for more information about recover by openzeppelin
   */

  function verifySignature(
    bytes32 message,
    bytes memory signature
  ) internal view returns (bool) {
    return
      hasRole(
        SIGNATORY_ROLE,
        message.toEthSignedMessageHash().recover(signature)
      );
  }

  /* _-_-_-_-_-_-_-_-_-_- owner logic _-_-_-_-_-_-_-_-_-_- */

  function pause() external onlyRole(ADMIN_ROLE) whenNotPaused {
    _pause();
  }

  function start() external onlyRole(ADMIN_ROLE) whenPaused {
    _unpause();
  }

  function changeVerifier(
    address oldVerifier,
    address newVerifier
  ) external onlyRole(ADMIN_ROLE) {
    revokeRole(SIGNATORY_ROLE, oldVerifier);
    grantRole(SIGNATORY_ROLE, newVerifier);
  }

  function withdrawEth() external onlyRole(ADMIN_ROLE) {
    require(address(this).balance != 0, "Zero ETH balance.");
    (bool sent, ) = payable(msg.sender).call{ value: address(this).balance }(
      ""
    );
    require(sent, "Failed to send Ether");
  }

  function withdrawPac() external onlyRole(ADMIN_ROLE) {
    require(token.balanceOf(address(this)) != 0, "Zero PAC balance.");
    token.transfer(msg.sender, token.balanceOf(address(this)));
  }
}
